nt to add this item to eCatalog Campaign '+itemID))		{							document.getElementById('loaderAnimation').style.display='block';			//CHECK IF ITEM ALREADY EXIST					var file = '<?php echo HTTP_PATH ?>eCatalog/itemOnSession/'+itemID;			var result ='';						var xmlhttp;			if (window.XMLHttpRequest)			{// code for IE7+, Firefox, Chrome, Opera, Safari			  xmlhttp=new XMLHttpRequest();			}			else			{// code for IE6, IE5			  xmlhttp=new ActiveXObject("Microsoft.XMLHTTP");			}						xmlhttp.onreadystatechange=function()			{			  if (xmlhttp.readyState==4 && xmlhttp.status==200)			  {				  if(xmlhttp.responseText=='exist')				  {					document.getElementById('loaderAnimation').style.display='none';					alert('Sorry Item already exist');				  }else{					document.getElementById('loaderAnimation').style.display='none';					ajaxAppend('<?php echo HTTP_PATH ?>eCatalog/pushItem/'+itemID,'selectedItems');				  }			  }			}			xmlhttp.open("GET",file,true);			xmlhttp.send();						//document.getElementById('loaderAnimation').style.display='none';		}	}		</script>			