   = 1; 					$page  = 1;					$page2 = 1;					$l 	   = $last;										while($l!=0)					{												//ACTIVE PAGE						$style="";						$page_link = HTTP_PATH."iLikeCampaign/votingCampaign/page/".$i++;						if($active_page==$page++)						{							$style="style='text-decoration:underline'";						}						echo  " <a href='$page_link' $style><li>". $page2++ ."</li></a> ";						$l--;					}				?>				<a href="<?php echo HTTP_PATH."iLikeCampaign/votingCampaign/page/".$last ?>"><li class="page-btn">LAST &raquo;</li></a>		</ul>	<?php } ?>    </div>		<script type="text/javascript">		function deleteOneItem(id)		{			if(confirm("Are you sure you want to delete this Campaign?"))			{				window.location = "<?php echo HTTP_PATH ?>iLikeCampaign/votingCampaign/deleteOneItem/"+ id;			}		}	</script>