gn:center">	<?php if($last>0){ ?>		<ul class="pagination">				<a href="<?php echo HTTP_PATH."report/iWant/page/1" ?>"><li class="page-btn" style="margin-right:2px;"> &laquo; FIRST </li></a> 				<?php 					//PAGNINATION					$i	   = 1; 					$page  = 1;					$page2 = 1;					$l 	   = $last;										while($l!=0)					{												//ACTIVE PAGE						$style="";						$page_link = HTTP_PATH."report/iWant/page/".$i++;						if($active_page==$page++)						{							$style="style='text-decoration:underline'";						}						echo  " <a href='$page_link' $style><li>". $page2++ ."</li></a> ";						$l--;					}				?>				<a href="<?php echo HTTP_PATH."report/iWant/page/".$last ?>"><li class="page-btn">LAST &raquo;</li></a>		</ul>	<?php } ?>    </div>	