			<?php echo $csrf ?>				<div class="clear"></div>				<table cellpadding="0" cellspacing="0" style="width:100%;margin: 0px auto;">									<tr style="border-radius: 6px;">						<th style="width:200px;"> STATUS NAME </th> 						<th style="width:150px;text-align:center;"> ACTION </th>   					</tr>										<?php 						$x=0;						foreach($data as $s)						{							$c = (($x++)%2) == 0 ? "class='alter'" :  ""; 							extract($s);							echo "<tr>";								echo "<td $c style='text-align:left;'>". $premiumName ."</td>";								echo "<td $c style='text-align:center;'>";									if($EDIT)											echo "<a href='".HTTP_PATH."itemDatabase/PremiumItemType/edit/".$premiumID."'>Edit</a> | ";									if($DELETE)											echo "<a style='cursor:pointer' onclick='deleteOneItem($premiumID)'>Delete</a> ";									echo"</td>";								echo child($premiumID,30,$EDIT,$DELETE);							echo "</tr>";						}																		function child($premiumID,$Ml,$EDIT,$DELETE)						{							$CI =& get_instance();							$sql = $CI->db->query("SELECT premiumItemType.id AS premiumID, premiumItemType.premiumTypeName AS premiumName  												   FROM premiumItemTypeRef INNER JOIN premiumItemType 												   ON premiumItemType.id =  premiumItemTypeRef.childID WHERE premiumItemTypeRef.parentID='$premiumID'");							$child = $sql->result_array();														$field='';							foreach($child as $c)							{								$cl =  "class='alter2'"; 								extract($c);								$field .= "<tr><td $cl style='padding-left:".$Ml."px;text-align:left;'> >>". $premiumName ."</td>";								$field .= "<td $cl style='text-align:center;'>";										if($EDIT)											$field .= "<a href='".HTTP_PATH."itemDatabase/PremiumItemType/edit/".$premiumID."'>Edit</a> |";										if($DELETE)											$field .= "<a style='cursor:pointer' onclick='deleteOneItem($premiumID)'>Delete</a> 										</td>";								$field .= child($premiumID,$Ml+20,$EDIT,$DELETE);							}														return $field;						}					?>				</table>			</form>			            </div>			        </div>		        <div class="clear"></div>    </div>			<br/>		<script type="text/javascript">		function deleteOneItem(id)		{			jConfirm("Are you sure you want to delete this Premium Item Type?","Alert",function(r){				if(r) window.location = "<?php echo HTTP_PATH ?>itemDatabase/PremiumItemType/deleteOneItem/"+ id;			});		}	</script>